import * as pkg from "pkg-dir";
import path from "path";
import regexParser from "regex-parser";
import { ServerOptions } from "./ServerOptions";
import * as interpolate from "regexp-capture-interpolation";
import forward from "http-forward";
import getport from "get-port-sync";
import * as shell from "shell-quote";
import * as child from "child_process";
import * as join from "url-join";

let root = pkg.sync();

export interface Dammit{
    port:number,
    servers:ServerOptions[]
    rewrite:string[]
}


let dammitPath = path.join(root,"dammit.json");


export function ParseRewrite(rule:string){
    let [regex, url, folder] = rule.split(" ")

    let test = regexParser(regex)
    return {test,redirect:url,folder};

}

import  express from "express";


export function Main(){
    let config:Dammit = require(dammitPath)
    
    
    let serverRef = getServerReference(config);




    
    let app = express();

    bootServer(app, config,serverRef);
    
}

function getServerReference(config: Dammit) {
    return config.servers.map(server => {
        let fullPath = path.join(root, server.path);
        let port = getport();
        let url = `http://localhost:${port}`;
        let [command, ...args] = shell.parse(server.cmd);
     

        let thread = child.spawn(command, args, {
            cwd: fullPath,
            stdio: "inherit",
            shell:true,
            env: {
                port
            }
        });
        return {
            fullPath,
            port,
            url
        };
    });
}


console.log(interpolate)

export function bootServer(app, config: Dammit,refs:ReturnType<typeof getServerReference>) {
    app.all("*", function (req, res, next) {


        let url = req.url;
        let rules = config.rewrite.map(rule => {
            return ParseRewrite(rule);
        });


        let [result] = rules.filter(rule => rule.test.test(url));
        let [serverInstance] = refs.filter(ref=>ref.fullPath== path.join(root,result?result.folder:''))
        if (!result || !serverInstance) {
            return next();
        }
        else {

            console.log(result,serverInstance)
            let destination = Array.from(result.test.exec(url))
            console.log(destination)
            let rewrittenPath = url.replace(result.test,result.redirect).replace(/\/{2,}/g,"/");
            let target = join(serverInstance.url,rewrittenPath);
            req["forward"] = { target };
            
            console.log(target);
            forward(req, res);
        }
    });

    const server = app.listen(process.env.port || getport(),()=>{
        console.log(`server running on http://localhost:${server.address().port}`)
    })
}

Main();
